/*
京东账号检测
cron 0 8,9 * * * jd_ckcheck.js
*/
const $ = new Env('京东账号检测');
const fs = require('fs');
const notify = require('./sendNotify');
const jdCookieNode = require('./jdCookie.js');
let cookiesArr = [];
let allMsg = '';
const CKGG = $.isNode() ? (process.env.CKGG ? process.env.CKGG : 'false') : 'false';  //默认不推送，失效CK强制推送
const REMIND_DAY = $.isNode() ? (process.env.REMIND_DAY ? process.env.REMIND_DAY : '2') : '2';  //提前几天推送
const GET_COOKIES_URL = $.isNode() ? (process.env.GET_COOKIES_URL ? process.env.GET_COOKIES_URL : 'https://bean.m.jd.com/bean/signIndex.action') : 'https://bean.m.jd.com/bean/signIndex.action';
const ONE_BY_ONE = $.isNode() ? (process.env.ONE_BY_ONE ? process.env.ONE_BY_ONE : 'false') : 'false';  //失效ck1对1推送，涉及其他文件修改，请不要开启

Object.keys(jdCookieNode).forEach((item) => {
  cookiesArr.push(jdCookieNode[item]);
});

!(async () => {
  console.log(`\n开始检测`);
  console.log(`1对1推送：${ONE_BY_ONE}`);
  console.log(`检测结果是否推送：${CKGG}`);
  console.log(`到期前${REMIND_DAY}天进行推送`);
  console.log(`更新CK地址：${GET_COOKIES_URL}\n`);
  fs.accessSync('../config/config.sh')
  remarks = fs.readFileSync('../config/config.sh').toString()
  var array = remarks.match(/## pt_pin=(.*?) 上次更新：(.*?) /g);
  //console.log(array);
  let nowTime = new Date(parseInt(new Date().getTime() + new Date().getTimezoneOffset() * 60 * 1000 + 8 * 60 * 60 * 1000)).toLocaleDateString();

  for (let i = 0; i < cookiesArr.length; i++) {
    $.cookie = cookiesArr[i];
    $.isLogin = true;
    $.UserName = decodeURIComponent($.cookie.match(/pt_pin=([^; ]+)(?=;?)/) && $.cookie.match(/pt_pin=([^; ]+)(?=;?)/)[1]);
    $.pt_pin = $.cookie.match(/pt_pin=([^; ]+)(?=;?)/)[1];
    $.update = '';
    $.remainder = 0;
    var reg = new RegExp(`## pt_pin=${$.pt_pin}; 上次更新：(.*?) `, 'gi');

    await TotalBean();
    if ($.isLogin) {
      if (reg.test(array)) {
        $.update = new Date(RegExp.$1).toLocaleDateString();
        $.remainder = 30 - DateDiff(nowTime, $.update)
        //console.log($.update);
      }
      if ($.update != '') {
        console.log(`京东账号 ${i + 1}：${$.UserName}\n更新日期：${$.update}\n剩余天数：${$.remainder}天`);
        console.log('----------');
        allMsg =
          allMsg +
          '\n----------\n' +
          `京东账号 ${i + 1}：${$.UserName}\n更新日期：${$.update}\n剩余天数：${$.remainder}天`;
        if ($.isNode() && $.remainder <= REMIND_DAY && ONE_BY_ONE == 'true') {
          await notify.sendNotify2(`${$.name}`, `京东账号：${$.UserName}\n更新日期：${$.update}\n剩余天数：${$.remainder}天\n请尽快点击下方链接更新\n${GET_COOKIES_URL}`, { url: `${GET_COOKIES_URL}` })
        } else if ($.isNode() && $.remainder <= REMIND_DAY) {
          await notify.sendNotify(`${$.name}`, `京东账号：${$.UserName}\n更新日期：${$.update}\n剩余天数：${$.remainder}天\n请尽快点击下方链接更新\n${GET_COOKIES_URL}`, { url: `${GET_COOKIES_URL}` })
        }
      } else {
        console.log(`京东账号 ${i + 1}：${$.UserName} \t有效`);
        allMsg =
          allMsg +
          '\n' +
          `京东账号 ${i + 1}：${$.UserName} \t有效`;
      }

    } else {
      console.log(`京东账号 ${i + 1}：${$.UserName} \t失效`);
      allMsg =
        allMsg +
        '\n' +
        `京东账号 ${i + 1}：${$.UserName} \t失效`;
      if ($.isNode() && ONE_BY_ONE == 'true') {
        await notify.sendNotify2(`${$.name}`, `京东账号 ${i + 1}：${$.UserName}\n已经失效\n请尽快点击下方链接更新\n${GET_COOKIES_URL}`, { url: `${GET_COOKIES_URL}` })
      } else if ($.isNode()) {
        await notify.sendNotify(`${$.name}`, `京东账号 ${i + 1}：${$.UserName}\n已经失效\n请尽快点击下方链接更新\n${GET_COOKIES_URL}`, { url: `${GET_COOKIES_URL}` })
      }
    }
  }
  if ($.isNode() && allMsg && CKGG == 'true') {
    allMsg.replace(/\n\n/ig, '\n')
    await notify.sendNotify(`${$.name}`, `${allMsg}`, { url: `${GET_COOKIES_URL}` })
  }
})()
  .catch((e) => {
    $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
  })
  .finally(() => {
    $.done();
  })

function TotalBean() {
  return new Promise(async (resolve) => {
    const options = {
      url: 'https://me-api.jd.com/user_new/info/GetJDUserInfoUnion',
      headers: {
        Host: 'me-api.jd.com',
        Accept: '*/*',
        Connection: 'keep-alive',
        Cookie: $.cookie,
        'User-Agent': $.isNode()
          ? process.env.JD_USER_AGENT
            ? process.env.JD_USER_AGENT
            : require('./USER_AGENTS').USER_AGENT
          : $.getdata('JDUA')
            ? $.getdata('JDUA')
            : 'jdapp;iPhone;9.4.4;14.3;network/4g;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1',
        'Accept-Language': 'zh-cn',
        Referer: 'https://home.m.jd.com/myJd/newhome.action?sceneval=2&ufc=&',
        'Accept-Encoding': 'gzip, deflate, br',
      },
    };
    $.get(options, (err, resp, data) => {
      try {
        if (err) {
          $.logErr(err);
        } else {
          if (data) {
            data = JSON.parse(data);
            if (data['retcode'] === '1001') {
              $.isLogin = false; //cookie过期
              return;
            }
            if (
              data['retcode'] === '0' &&
              data.data &&
              data.data.hasOwnProperty('userInfo')
            ) {
              $.nickName = data.data.userInfo.baseInfo.nickname;
            }
          } else {
            $.log('京东服务器返回空数据');
          }
        }
      } catch (e) {
        $.logErr(e);
      } finally {
        resolve();
      }
    });
  });
}

function DateDiff(sDate1, sDate2) {    //sDate1和sDate2是2002-12-18格式 
  var aDate, oDate1, oDate2, iDays
  aDate = sDate1.split("-")
  oDate1 = new Date(aDate[1] + '-' + aDate[2] + '-' + aDate[0])    //转换为12-18-2002格式 
  aDate = sDate2.split("-")
  oDate2 = new Date(aDate[1] + '-' + aDate[2] + '-' + aDate[0])
  iDays = parseInt(Math.abs(oDate1 - oDate2) / 1000 / 60 / 60 / 24)    //把相差的毫秒数转换为天数 
  return iDays
}
// prettier-ignore
